import React from 'react'



const CategorySelect = ({selectedCategory, setSelectedCategory, transactions}) => {

    const handleChange = function(event) {
        let chosenCategory = event.target.value;
        setSelectedCategory(chosenCategory)
        // transHistory()
    }

    // const transHistory = () => {
    //     const foundTransaction = transactions.filter(transaction => transaction.category === selectedCategory)
    //     return foundTransaction
    // }





    return (
        <div className="category-select">
            <h2>hello</h2>
            <form>
                <ul>
                    <li>
                        <label for="category">Category:</label>
                        <br></br>
                        <select name="category" onChange={handleChange} required>
                            <option>Morrisons</option>
                            <option>CYBG</option>
                            <option>Pure Gym</option>
                            <option>M&S</option>
                            <option>McMillan</option>
                        </select> 
                    </li>
                </ul>
                <h2>History</h2>
                <ul>
                    {transactions.filter(transaction => transaction.category === "Morrisons" (
                        <li>{transaction.date}</li>
                    ))}
                </ul>
            </form>


        </div>
    )
}

export default CategorySelect
